import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = () => {
  const [selectedYear, setSelectedYear] = useState("");
  const yearChangeHandler = (event) => {
    setSelectedYear(event.target.value);
  };

  const submitSelectedYear = (event) => {
    event.preventDefault();
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select defaultValue="2020">
          <option value="2022" onSelect={yearChangeHandler}>
            2022
          </option>
          <option value="2021" onSelect={yearChangeHandler}>
            2021
          </option>
          <option value="2020" onSelect={yearChangeHandler}>
            2020
          </option>
          <option value="2019" onSelect={yearChangeHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
